        public bool sendEmail(string to, string toName, string body, string subject, string extraCC)
        {
            try
            {
                var fromAddress = new MailAddress("noreply@lidlaunch.com", "Lid Launch");
                var toAddress = new MailAddress(to, toName);
                
                var smtp = new SmtpClient
                {
                    Host = "smtp.gmail.com",
                    Port = 587,
                    EnableSsl = true,
                    DeliveryMethod = SmtpDeliveryMethod.Network,
                    UseDefaultCredentials = false,
                    Credentials = new NetworkCredential("robert@lidlaunch.com", "Z06Corvette90!")
                };
                using (var message = new MailMessage(fromAddress, toAddress)
                {
                    Subject = subject,
                    Body = body,

                })
                {
                    message.IsBodyHtml = true;
                    message.Bcc.Add("robert@lidlaunch.com");
                    if(extraCC != "")
                    {
                        message.CC.Add(extraCC);
                    }
                    smtp.Send(message);
                }

                return true;
            }
            catch (Exception ex)
            {
                Logger.Log("Error Sending Email: " + ex.Message.ToString() + "::::" + ex.InnerException.Message.ToString());
                return false;
            }
        }
    }
